import random
p,q = input("Enter two large prime numbers").split(" ")
p = int(p)
q = int(q)
print (p,q)

n = p*q
print("value of n is:",n)

# Calculating Totient
tot = (p-1) * (q-1)
print("totient is :",tot)

# calculating e
def GCD(a,b):
    if(a>b):
        small = b
    else:
        small = a
    for i in range(1,small+1):
        if((a%i == 0) and (b%i == 0)):
            gcd = i
    return gcd    
L=[]
for i in range(1,tot):
    g=GCD(i,tot)
    if(g == 1):
        print(" ",i)
        L.append(i)
print(L)
e = random.choice(L)
print("e is :",e)
def modInverse(a,m):
    a = a% m
    for i in range(1,m):
        if((a*i)%m ==1):
            return i

d = modInverse(e,tot)
print("private key is :",d)

# Encryption

msg = input("Enter the message")
c=[]
for i in range(0,len(msg)):
    # print(msg[i])
    # temp[i] = ord(msg[i])
    v = ord(msg[i])
    c.append((v ** e) % n)

# Encrypted msg
print("Encrypted msg is :",c)

# Decryption
temp = []
for i in range(0,len(c)):
    temp.append((c[i] ** d) % n)
print("Decrypted msg is :",temp)
a = []
for i in range(0,len(temp)):
    a.append(chr(temp[i]))
   
print (''.join(a))

